sp_widget.client_script:
function($scope, $rootScope){
	var c = this;

	$rootScope.$on('selectNote', function(event, data){
		console.log('Listener caught selectNote: ' + $rootScope.noteID);

		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		})
			.then(function(r){
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.updated = r.data.note.updated;
			c.data.noteID = r.data.note.sys_id;
		})
		console.log('c.data.title: ' + c.data.title);
		console.log('c.data.note: ' + c.data.note);
		console.log('c.data.updated: ' + c.data.updated);
	});

	c.updateNote = function(updateType){
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			inputTitle: c.data.title,
			inputNote: c.data.note
		})
		$rootScope.$emit('updateNote', $rootScope.noteID);
		console.log("Emitted updateNote: " + $rootScope.noteID);
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
	// if we have input, check we have the ID
	if(input && input.noteID){
		// get the relevant record
		var noteGR = new GlideRecord('x_snc_createnotes_note');
		if(noteGR.get(input.noteID)){
			// if we are retreiving...
			if(input.action == 'getNote'){
				data.note = {};
				data.note.title = noteGR.getValue('title');
				data.note.note = noteGR.getValue('note');
				data.note.updated = noteGR.getValue('sys_updated_on');
				data.note.sys_id = noteGR.getValue('sys_id');
			}
			// if we are updating...
			else if(input.action == 'updateNote'){
				noteGR.title = input.inputTitle;
				noteGR.note = input.inputNote;
				noteGR.update();
			}
		}
	}
})();
